    var slider = $('.bx-insta').bxSlider({
      mode: 'fade',
      nextText: '»',
      prevText: '«',
      captions: true,
      responsive: true
    });

    slider.goToSlide(index);

    setTimeout(function(){
      $('.bx-pager-link').html('');
    }, 100);

  });

  $('body').on('click', '#close-insta', function(){
    $('#insta-modal-gallery').fadeOut().addClass('hide');
    slider = $('.bx-insta').bxSlider();
    slider.destroySlider();
    $('#insta-modal-gallery').html('');
  });

});

